use Leevel\Support\Fn;

if (!function_exists('fn')) {
	/**
	 * 自动导入函数.
	 *
	 * @param \Closure|string $call
	 * @param array           $args
	 * @param mixed           $fn
	 *
	 * @return mixed
	 */
	function fn($fn, ...$args)
	{
		return (new Fn())($fn, ...$args);
	}
}

